          <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '3rem'}}>
            <h2>Little Bamiyan Blog</h2>
            {isAdmin && (
              <button onClick={() => setShowBlogForm(true)} className="add-btn">
