                    <div className="admin-controls" onClick={(e) => e.stopPropagation()}>
                      <button onClick={(e) => {
                        e.stopPropagation();
                        setEditingBlog(blog);
                        setShowBlogForm(true);
                      }} className="admin-btn">
                        <Edit size={16} />
