        <div className="footer-nav">
          <div className="footer-nav-column">
            <h4>Explore</h4>
            <a href="#" onClick={(e) => { e.preventDefault(); setCurrentPage('home'); }}>Home</a>
            <a href="#" onClick={(e) => { 
              e.preventDefault(); 
              setCurrentPage('home'); 
              setTimeout(() => {
                const element = document.getElementById('about');
                if (element) element.scrollIntoView({behavior: 'smooth'});
              }, 100); 
            }}>About</a>
            <a href="#" onClick={(e) => { 
              e.preventDefault(); 
              setCurrentPage('home'); 
              setTimeout(() => {
                const element = document.getElementById('businesses');
                if (element) element.scrollIntoView({behavior: 'smooth'});
              }, 100); 
            }}>Businesses</a>
            <a href="#" onClick={(e) => { 
              e.preventDefault(); 
              setCurrentPage('home'); 
              setTimeout(() => {
                const element = document.getElementById('events');
                if (element) element.scrollIntoView({behavior: 'smooth'});
              }, 100); 
            }}>Events</a>
            <a href="#" onClick={(e) => { 
              e.preventDefault(); 
              setCurrentPage('home'); 
              setTimeout(() => {
                const element = document.getElementById('visit');
                if (element) element.scrollIntoView({behavior: 'smooth'});
              }, 100); 
            }}>Visit</a>
          </div>

          <div className="footer-nav-column">
            <h4>Community</h4>
            <a href="#" onClick={(e) => { e.preventDefault(); setCurrentPage('blog'); setSelectedBlog(null); }}>Hazara News</a>
            <a href="#">Cultural Resources</a>
            <a href="#">Hazara Association</a>
            <a href="#">Volunteer</a>
