  // Main render
  return (
    <div className="main-content">
      <Navigation />
      {showBusinessForm && isAdmin && <BusinessForm />}
      {showBlogForm && isAdmin && <BlogForm />}
      {selectedBusiness && <BusinessPreviewModal business={selectedBusiness} onClose={() => setSelectedBusiness(null)} />}
      {currentPage === 'home' ? (
        <>
          <Hero />
          <About />
          <Businesses />
          <Events />
          <Visit />
        </>
      ) : currentPage === 'blog' ? (
        <BlogSection />
      ) : currentPage === 'login' ? (
        <LoginPage />
      ) : currentPage === 'admin' ? (
        <AdminPage />
      ) : null}
      <Footer />
    </div>
  );
};

export default App;
