import React, { useState, useEffect } from 'react';
import { Edit, Plus, X, LogOut } from 'lucide-react';

const App = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [currentPage, setCurrentPage] = useState('home');
  const [businesses, setBusinesses] = useState([
    {
      id: 1,
      name: 'Hazara House Restaurant',
      category: 'Restaurant',
      description: 'Authentic Hazara cuisine featuring traditional mantu, ashak, and quroot dishes prepared with family recipes passed down generations.',
      address: '12 Thomas Street',
      phone: '(03) 9793 1234',
      rating: 4.8,
      image: '/api/placeholder/400/220'
    },
    {
      id: 2,
      name: 'Bamiyan Naan Bakery',
      category: 'Bakery',
      description: 'Traditional Hazara breads, including roghani naan, kolcheh, and specialty pastries made fresh daily using ancestral methods.',
      address: '18 Thomas Street',
      phone: '(03) 9791 2345',
      rating: 4.9,
      image: '/api/placeholder/400/220'
    },
    {
      id: 3,
      name: 'Hazaristan Market',
      category: 'Grocery',
      description: 'Specialty store offering Hazara ingredients, dried fruits, pickles, and traditional spices essential for authentic Hazara cooking.',
      address: '24 Thomas Street',
      phone: '(03) 9793 3456',
      rating: 4.7,
      image: '/api/placeholder/400/220'
    }
  ]);
  const [blogs, setBlogs] = useState([
    {
      id: 1,
      title: 'The History of Bamiyan Valley',
      excerpt: 'Explore the rich cultural heritage of the Bamiyan Valley, the ancestral homeland of the Hazara people...',
      date: '2025-03-15',
      author: 'Admin',
      image: '/api/placeholder/400/250',
      content: 'Full blog content here...'
    }
  ]);
  const [showAdmin, setShowAdmin] = useState(false);
  const [loginCredentials, setLoginCredentials] = useState({ username: '', password: '' });
  const [showBusinessForm, setShowBusinessForm] = useState(false);
  const [showBlogForm, setShowBlogForm] = useState(false);
  const [editingBusiness, setEditingBusiness] = useState(null);
  const [editingBlog, setEditingBlog] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 100);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Admin Functions
  const handleLogin = (e) => {
    e.preventDefault();
    if (loginCredentials.username === 'admin' && loginCredentials.password === 'password') {
      setIsAdmin(true);
      setShowAdmin(false);
      setLoginCredentials({ username: '', password: '' });
    } else {
      alert('Invalid credentials. Use username: admin, password: password');
    }
  };

  const handleLogout = () => {
    setIsAdmin(false);
    setCurrentPage('home');
  };

  const handleAddBusiness = (business) => {
    if (editingBusiness) {
      setBusinesses(businesses.map(b => b.id === editingBusiness.id ? { ...business, id: b.id } : b));
      setEditingBusiness(null);
    } else {
      setBusinesses([...businesses, { ...business, id: Date.now() }]);
    }
    setShowBusinessForm(false);
  };

  const handleDeleteBusiness = (id) => {
    if (window.confirm('Are you sure you want to delete this business?')) {
      setBusinesses(businesses.filter(b => b.id !== id));
    }
  };

  const handleAddBlog = (blog) => {
    if (editingBlog) {
      setBlogs(blogs.map(b => b.id === editingBlog.id ? { ...blog, id: b.id } : b));
      setEditingBlog(null);
    } else {
      setBlogs([...blogs, { ...blog, id: Date.now(), date: new Date().toISOString().split('T')[0], author: 'Admin' }]);
    }
    setShowBlogForm(false);
  };

  const handleDeleteBlog = (id) => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      setBlogs(blogs.filter(b => b.id !== id));
    }
  };

  // Components
  const Navigation = () => (
    <nav className={`fixed top-0 left-0 right-0 bg-white z-50 transition-all duration-300 ${isScrolled ? 'shadow-lg' : ''}`}>
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center py-3">
          <div className="flex items-center">
            <img src="/api/placeholder/80/80" alt="Logo" className="w-20 h-auto mr-4" />
            <div>
              <h3 className="text-xl font-bold text-[#D14809]">Little Bamiyan</h3>
              <div className="text-sm text-[#691C15]">Hazara Cultural Heritage Center</div>
            </div>
          </div>
          <button 
            className="lg:hidden text-[#D14809] text-2xl"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            ☰
          </button>
          <ul className={`${isMenuOpen ? 'flex' : 'hidden'} lg:flex flex-col lg:flex-row absolute lg:relative top-full left-0 right-0 bg-white lg:bg-transparent shadow-lg lg:shadow-none p-4 lg:p-0 space-y-2 lg:space-y-0 lg:space-x-4`}>
            <li><a href="#home" onClick={() => setCurrentPage('home')} className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">Home</a></li>
            <li><a href="#about" className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">About</a></li>
            <li><a href="#businesses" className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">Businesses</a></li>
            <li><a href="#events" className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">Events</a></li>
            <li><a href="#" onClick={(e) => { e.preventDefault(); setCurrentPage('blog'); }} className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">Blog</a></li>
            <li><a href="#visit" className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block">Visit Us</a></li>
            {isAdmin ? (
              <li>
                <button 
                  onClick={handleLogout}
                  className="flex items-center text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2"
                >
                  <LogOut className="w-4 h-4 mr-2" />
                  Logout
                </button>
              </li>
            ) : (
              <li>
                <button 
                  onClick={() => setShowAdmin(true)}
                  className="text-[#691C15] hover:text-[#D14809] font-semibold px-4 py-2 block"
                >
                  Admin
                </button>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );

  const AdminModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-8 rounded-lg max-w-md w-full">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-[#D14809]">Admin Login</h2>
          <button onClick={() => setShowAdmin(false)} className="text-gray-500 hover:text-gray-700">
            <X className="w-6 h-6" />
          </button>
        </div>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Username</label>
            <input 
              type="text" 
              className="w-full p-2 border rounded"
              value={loginCredentials.username}
              onChange={(e) => setLoginCredentials({...loginCredentials, username: e.target.value})}
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 mb-2">Password</label>
            <input 
              type="password" 
              className="w-full p-2 border rounded"
              value={loginCredentials.password}
              onChange={(e) => setLoginCredentials({...loginCredentials, password: e.target.value})}
            />
          </div>
          <button 
            type="submit"
            className="w-full bg-[#D14809] text-white py-2 rounded hover:bg-[#691C15] transition-colors"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );

  const BusinessForm = () => {
    const [formData, setFormData] = useState(editingBusiness || {
      name: '',
      category: '',
      description: '',
      address: '',
      phone: '',
      rating: 4.5,
      image: '/api/placeholder/400/220'
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      handleAddBusiness(formData);
    };

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white p-8 rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-[#D14809]">{editingBusiness ? 'Edit Business' : 'Add New Business'}</h2>
            <button onClick={() => {
              setShowBusinessForm(false);
              setEditingBusiness(null);
            }} className="text-gray-500 hover:text-gray-700">
              <X className="w-6 h-6" />
            </button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 mb-2">Business Name</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Category</label>
                <select 
                  className="w-full p-2 border rounded"
                  value={formData.category}
                  onChange={(e) => setFormData({...formData, category: e.target.value})}
                  required
                >
                  <option value="">Select Category</option>
                  <option value="Restaurant">Restaurant</option>
                  <option value="Bakery">Bakery</option>
                  <option value="Grocery">Grocery</option>
                  <option value="Fashion">Fashion</option>
                  <option value="Cafe">Cafe</option>
                  <option value="Services">Services</option>
                </select>
              </div>
              <div className="md:col-span-2">
                <label className="block text-gray-700 mb-2">Description</label>
                <textarea 
                  className="w-full p-2 border rounded"
                  rows="3"
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Address</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.address}
                  onChange={(e) => setFormData({...formData, address: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Phone</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.phone}
                  onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Rating</label>
                <input 
                  type="number" 
                  step="0.1"
                  min="0"
                  max="5"
                  className="w-full p-2 border rounded"
                  value={formData.rating}
                  onChange={(e) => setFormData({...formData, rating: parseFloat(e.target.value)})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Image URL</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.image}
                  onChange={(e) => setFormData({...formData, image: e.target.value})}
                  placeholder="/api/placeholder/400/220"
                />
              </div>
            </div>
            <button 
              type="submit"
              className="w-full mt-6 bg-[#D14809] text-white py-2 rounded hover:bg-[#691C15] transition-colors"
            >
              {editingBusiness ? 'Update Business' : 'Add Business'}
            </button>
          </form>
        </div>
      </div>
    );
  };

  const BlogForm = () => {
    const [formData, setFormData] = useState(editingBlog || {
      title: '',
      excerpt: '',
      content: '',
      image: '/api/placeholder/400/250'
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      handleAddBlog(formData);
    };

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white p-8 rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-[#D14809]">{editingBlog ? 'Edit Blog Post' : 'Add New Blog Post'}</h2>
            <button onClick={() => {
              setShowBlogForm(false);
              setEditingBlog(null);
            }} className="text-gray-500 hover:text-gray-700">
              <X className="w-6 h-6" />
            </button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label className="block text-gray-700 mb-2">Title</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.title}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Excerpt</label>
                <textarea 
                  className="w-full p-2 border rounded"
                  rows="2"
                  value={formData.excerpt}
                  onChange={(e) => setFormData({...formData, excerpt: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Content</label>
                <textarea 
                  className="w-full p-2 border rounded"
                  rows="10"
                  value={formData.content}
                  onChange={(e) => setFormData({...formData, content: e.target.value})}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Image URL</label>
                <input 
                  type="text" 
                  className="w-full p-2 border rounded"
                  value={formData.image}
                  onChange={(e) => setFormData({...formData, image: e.target.value})}
                  placeholder="/api/placeholder/400/250"
                />
              </div>
            </div>
            <button 
              type="submit"
              className="w-full mt-6 bg-[#D14809] text-white py-2 rounded hover:bg-[#691C15] transition-colors"
            >
              {editingBlog ? 'Update Blog Post' : 'Add Blog Post'}
            </button>
          </form>
        </div>
      </div>
    );
  };

  const Hero = () => (
    <section className="relative min-h-screen flex items-center">
      <div className="absolute inset-0 bg-cover bg-center" style={{backgroundImage: 'url(/api/placeholder/1920/1080)'}}></div>
      <div className="absolute inset-0 bg-gradient-to-r from-[#D14809]/80 to-[#691C15]/70"></div>
      <div className="relative max-w-7xl mx-auto px-4 z-10">
        <div className="max-w-3xl">
          <h1 className="text-5xl md:text-6xl font-bold text-white mb-6">Experience Little Bamiyan</h1>
          <p className="text-xl md:text-2xl text-white mb-8">Discover the vibrant culture, authentic cuisine, and warm hospitality of Melbourne's Hazara cultural heart</p>
          <a href="#businesses" className="inline-block bg-[#FFB700] text-[#691C15] font-bold py-3 px-8 rounded-full hover:bg-transparent hover:text-[#FFB700] border-2 border-[#FFB700] transition-all">
            Explore The Bazaar
          </a>
        </div>
      </div>
    </section>
  );

  const About = () => (
    <section id="about" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4">
        <h2 className="text-4xl font-bold text-center text-[#691C15] mb-12">About Little Bamiyan</h2>
        <div className="flex flex-col md:flex-row items-center gap-12">
          <div className="md:w-1/2 space-y-6">
            <p className="text-lg text-gray-600">
              Little Bamiyan, named after the historic valley in central Afghanistan that has been home to Hazara people for centuries, is Melbourne's premier Hazara cultural precinct on Thomas Street in Dandenong. Our community honors the resilience, culture, and traditions of the Hazara people.
            </p>
            <p className="text-lg text-gray-600">
              Since the late 1990s, Hazara families have transformed this area into a vibrant hub celebrating our distinctive culture, language, and heritage. Our community's journey from the mountains of central Afghanistan to Melbourne has enriched the multicultural tapestry of Greater Dandenong.
            </p>
            <p className="text-lg text-gray-600">
              Today, Little Bamiyan serves as a cultural bridge, offering authentic Hazara cuisine, traditional music and arts, and a warm community spirit that welcomes all visitors to experience our unique heritage and centuries-old traditions.
            </p>
          </div>
          <div className="md:w-1/2">
            <img src="/api/placeholder/600/400" alt="Little Bamiyan" className="rounded-lg shadow-lg w-full" />
          </div>
        </div>
      </div>
    </section>
  );

  const Businesses = () => (
    <section id="businesses" className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center mb-12">
          <h2 className="text-4xl font-bold text-[#691C15]">Business Directory</h2>
          {isAdmin && (
            <button 
              onClick={() => setShowBusinessForm(true)}
              className="flex items-center bg-[#D14809] text-white px-4 py-2 rounded hover:bg-[#691C15] transition-colors"
            >
              <Plus className="w-5 h-5 mr-2" />
              Add Business
            </button>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {businesses.map(business => (
            <div key={business.id} className="bg-white rounded-lg overflow-hidden shadow-lg hover:shadow-xl transition-shadow">
              <div className="relative">
                <img src={business.image} alt={business.name} className="w-full h-56 object-cover" />
                <span className="absolute top-4 left-4 bg-gradient-to-r from-[#D14809] to-[#FFB700] text-white px-4 py-1 rounded-full text-sm font-semibold">
                  {business.category}
                </span>
                {isAdmin && (
                  <div className="absolute top-4 right-4 flex space-x-2">
                    <button 
                      onClick={() => {
                        setEditingBusiness(business);
                        setShowBusinessForm(true);
                      }}
                      className="bg-white p-2 rounded-full shadow hover:bg-gray-100"
                    >
                      <Edit className="w-4 h-4 text-[#691C15]" />
                    </button>
                    <button 
                      onClick={() => handleDeleteBusiness(business.id)}
                      className="bg-white p-2 rounded-full shadow hover:bg-gray-100"
                    >
                      <X className="w-4 h-4 text-red-500" />
                    </button>
                  </div>
                )}
              </div>
              <div className="p-6">
                <h3 className="text-xl font-bold text-[#D14809] mb-2">{business.name}</h3>
                <p className="text-gray-600 mb-4">{business.description}</p>
                <p className="text-gray-700 mb-2"><strong>Address:</strong> {business.address}</p>
                <div className="flex justify-between items-center border-t pt-4 mt-4">
                  <div className="flex items-center text-[#691C15] font-semibold">
                    ★★★★★ {business.rating}
                  </div>
                  <a href={`tel:${business.phone}`} className="text-[#D14809] font-medium hover:text-[#691C15]">
                    📞 {business.phone}
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );

  const Events = () => (
    <section id="events" className="py-20 bg-[#D14809] text-white relative overflow-hidden">
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-0 right-0 w-64 h-64 rounded-full bg-white transform translate-x-1/2 -translate-y-1/2"></div>
        <div className="absolute bottom-0 left-0 w-48 h-48 rounded-full bg-white transform -translate-x-1/2 translate-y-1/2"></div>
      </div>
      <div className="max-w-7xl mx-auto px-4 relative z-10">
        <h2 className="text-4xl font-bold text-center mb-12">Upcoming Events</h2>
        <div className="flex overflow-x-auto pb-6 space-x-6 snap-x snap-mandatory">
          <div className="flex-none w-80 bg-white/10 backdrop-blur-lg rounded-lg p-6 border border-white/20 snap-start">
            <div className="text-[#FFB700] font-bold mb-4">May 15, 2025</div>
            <h3 className="text-xl font-bold mb-4">Hazara Cultural Festival</h3>
            <p className="mb-6">Annual celebration of Hazara heritage featuring traditional dambura music, folkloric dances, authentic cuisine, and cultural exhibitions.</p>
            <a href="#" className="text-white font-semibold inline-flex items-center hover:text-[#FFB700]">
              Learn more →
            </a>
          </div>
          <div className="flex-none w-80 bg-white/10 backdrop-blur-lg rounded-lg p-6 border border-white/20 snap-start">
            <div className="text-[#FFB700] font-bold mb-4">June 21, 2025</div>
            <h3 className="text-xl font-bold mb-4">Nowroz Celebration</h3>
